mapper.select:
SELECT
FROM sys_temdetails d LEFT JOIN tem_temdetails tt ON d.templatedetails_id = tt.templatedetails_id LEFT JOIN sys_template t ON tt.template_id = t.template_id
AND t.template_id =#{templateid}
AND t.template_name =#{templatename}
AND t.template_status =#{templatestatus}
ORDER BY d.templatedetails_status DESC, d.createtime DESC

SELECT
FROM sys_temdetails d LEFT JOIN tem_temdetails td ON d.templatedetails_id = td.templatedetails_id LEFT JOIN sys_template t ON t.template_id = td.template_id LEFT JOIN user_template ut ON td.template_id = ut.template_id LEFT JOIN sys_user u ON ut.user_id = u.user_id
t.template_status = 1
AND u.user_id= #{userId}
AND u.user_name =#{username}

SELECT

FROM sys_temdetails d
LEFT JOIN tem_temdetails tt ON d.templatedetails_id = tt.templatedetails_id


        AND d.templatedetails_keyword =#{keyword}


        AND d.templatedetails_value =#{keywordToValue}


        AND tt.template_id =#{templateId}

    ORDER BY d.createtime
